from svg2gakko.parser import svg2base64gakko
from pathlib import Path


def test_parse_svg_returns_str():
    result = svg2base64gakko(Path("data/example.svg"))
    assert isinstance(result, str)


def test_parse_svg():
    assert (
        svg2base64gakko(Path("data/example.svg"))
        == """<img style="width: 236px" src="data:image/jpeg;base64,"><br>"""
    )
